package com.example.myfragment;

import android.support.v4.app.Fragment;
import android.support.v4.app.FragmentManager;
import android.support.v4.app.FragmentPagerAdapter;
import android.support.v4.view.ViewPager;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private List<Fragment> mData = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        List<Model> movieDataList = new ArrayList<>();
        movieDataList.add(new Model("data:image/jpeg;base64,", "셜록"));
        movieDataList.add(new Model("http://image2.megabox.co.kr/mop/poster/2019/FF/35FB82-03E7-4407-95A2-49C9E59B7365.large.jpg" ,"캡틴마블"));
        movieDataList.add(new Model("https://img4.yna.co.kr/etc/inner/KR/2018/03/09/AKR20180309176900005_01_i_P2.jpg" ,"플레이어" ));
        movieDataList.add(new Model("https://img.sbs.co.kr/newsnet/etv/upload/2018/06/26/30000605680_1280.jpg","마녀"));
        movieDataList.add(new Model("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgufdiJKeBbvP13lgLNg2DkGHKVyzpawVAMHezSDg7i0PvXv_13w","스코어"));

        List<Fragment> movieFragmentList = new ArrayList<>();
        for (Model model : movieDataList) {
            FragmentView fragmentView = FragmentView.newInstance(model);
            movieFragmentList.add(fragmentView);
        }

        MyAdapter adapter = new MyAdapter(getSupportFragmentManager(), movieFragmentList);

        ViewPager viewPager = findViewById(R.id.pag);
        viewPager.setAdapter(adapter);

    }

    private static class MyAdapter extends FragmentPagerAdapter {
        private List<Fragment> mData;

        public MyAdapter(FragmentManager fm, List<Fragment> data) {
            super(fm);
            this.mData = data;
        }

        @Override
        public Fragment getItem(int i) {
            return mData.get(i);
        }

        @Override
        public int getCount() {
            return mData.size();
        }
    }
}
